import React, { useState } from "react"; // Import useState from react
import './Home.css';
import aw from './aw.gif'; // Import the GIF file

const Beval = () => {
    const [clicked, setClicked] = useState(false); // State to track click

    const handleClick = () => {
        if (clicked == 0)
        {
            setClicked(1);
        }
        else
        {
            setClicked(0);
        }
    };
    return (
        <div className="valentine">
        <img 
            src={aw} 
            alt="GIF" 
            style={{ width: '600px', height: 'auto', cursor: 'pointer' }} 
        />
        onClick={handleClick} 
           {clicked ? (
                <div className="question" style={{ color: '#e51d1d' }}>
                    Will you be my valentine?
                </div>
            ) : (
                <div className="question" style={{ color: '#ffd4d4' }}>
                    Will you be my valentine?
                </div>
            )}
    </div>
    );
}
export { Beval };
