import React, { useState, useEffect } from 'react';
import './Coupon.css';

const Coupon = () => {
  const [list] = useState([
    "$100",
    "$500",
    "$9,999",
    "$1",
    "$60",
    "$1,000",
    "$4.44",
    "$0",
    "$333"
  ]);
  const [radius] = useState(75); // PIXELS
  const [rotate, setRotate] = useState(0); // DEGREES
  const [easeOut, setEaseOut] = useState(0); // SECONDS
  const [angle, setAngle] = useState(0); // RADIANS
  const [top, setTop] = useState(null); // INDEX
  const [offset, setOffset] = useState(null); // RADIANS
  const [net, setNet] = useState(null); // RADIANS
  const [result, setResult] = useState(null); // INDEX
  const [spinning, setSpinning] = useState(false);

  useEffect(() => {
    renderWheel();
  }, []);

  const renderWheel = () => {
    const numOptions = list.length;
    const arcSize = (2 * Math.PI) / numOptions;
    setAngle(arcSize);

    topPosition(numOptions, arcSize);

    let angle = 0;
    for (let i = 0; i < numOptions; i++) {
      const text = list[i];
      renderSector(i + 1, text, angle, arcSize, getColor());
      angle += arcSize;
    }
  };

  const topPosition = (num, angle) => {
    let topSpot = null;
    let degreesOff = null;
    if (num === 9) {
      topSpot = 7;
      degreesOff = Math.PI / 2 - angle * 2;
    } else if (num === 8) {
      topSpot = 6;
      degreesOff = 0;
    } else if (num <= 7 && num > 4) {
      topSpot = num - 1;
      degreesOff = Math.PI / 2 - angle;
    } else if (num === 4) {
      topSpot = num - 1;
      degreesOff = 0;
    } else if (num <= 3) {
      topSpot = num;
      degreesOff = Math.PI / 2;
    }

    setTop(topSpot - 1);
    setOffset(degreesOff);
  };

  const renderSector = (index, text, start, arc, color) => {
    const canvas = document.getElementById("wheel");
    const ctx = canvas.getContext("2d");
    const x = canvas.width / 2;
    const y = canvas.height / 2;
    const radius = 3.33 * radius;
    const startAngle = start;
    const endAngle = start + arc;
    const baseSize = radius * 3.33;
    const textRadius = baseSize - 150;

    ctx.beginPath();
    ctx.arc(x, y, radius, startAngle, endAngle, false);
    ctx.lineWidth = radius * 2;
    ctx.strokeStyle = color;

    ctx.font = "17px Arial";
    ctx.fillStyle = "black";
    ctx.stroke();

    ctx.save();
    ctx.translate(
      baseSize + Math.cos(index * arc - arc / 2) * textRadius,
      baseSize + Math.sin(index * arc - arc / 2) * textRadius
    );
    ctx.rotate(index * arc - arc / 2 + Math.PI / 2);
    ctx.fillText(text, -ctx.measureText(text).width / 2, 0);
    ctx.restore();
  };

  const getColor = () => {
    let r = Math.floor(Math.random() * 255);
    let g = Math.floor(Math.random() * 255);
    let b = Math.floor(Math.random() * 255);
    return `rgba(${r},${g},${b},0.4)`;
  };

  const spin = () => {
    let randomSpin = Math.floor(Math.random() * 900) + 500;
    setRotate(randomSpin);
    setEaseOut(2);
    setSpinning(true);

    setTimeout(() => {
      getResult(randomSpin);
    }, 2000);
  };

  const getResult = (spin) => {
    const netRotation = ((spin % 360) * Math.PI) / 180;
    const { angle, top, offset } = this.state;
    let travel = netRotation + offset;
    let count = top + 1;
    while (travel > 0) {
      travel = travel - angle;
      count--;
    }
    let result;
    if (count >= 0) {
      result = count;
    } else {
      result = list.length + count;
    }
    setNet(netRotation);
    setResult(result);
  };

  const reset = () => {
    setRotate(0);
    setEaseOut(0);
    setResult(null);
    setSpinning(false);
  };

  return (
    <div className="App">
      <h1>Spinning Prize Wheel React</h1>
      <span id="selector">&#9660;</span>
      <canvas
        id="wheel"
        width="500"
        height="500"
        style={{
          WebkitTransform: `rotate(${rotate}deg)`,
          WebkitTransition: `-webkit-transform ${easeOut}s ease-out`
        }}
      />

      {spinning ? (
        <button type="button" id="reset" onClick={reset}>
          reset
        </button>
      ) : (
        <button type="button" id="spin" onClick={spin}>
          spin
        </button>
      )}
      <div className="display">
        <span id="readout">
          YOU WON:{"  "}
          <span id="result">{list[result]}</span>
        </span>
      </div>
    </div>
  );
};

export {Coupon};
