import React, { useState } from 'react';
import './Coupon.css';

const Coupon = () => {
  const [rotation, setRotation] = useState(0);
  const [spinning, setSpinning] = useState(false);

  const spinWheel = () => {
    if (!spinning) {
      const randomRotation = Math.floor(Math.random() * 360) + 360 * 5; // Spin at least 5 full rotations
      setRotation(randomRotation);
      setSpinning(true);
      setTimeout(() => setSpinning(false), 5000); // Stop spinning after 5 seconds
    }
  };

  return (
    <div className="wheel-container">
      <div className={`wheel ${spinning ? 'spinning' : ''}`} style={{ transform: `rotate(${rotation}deg)` }}>
        <div className="slice" style={{ transform: `rotate(-72deg)` }}>Section 1</div>
        <div className="slice" style={{ transform: `rotate(-144deg)` }}>Section 2</div>
        <div className="slice" style={{ transform: `rotate(-216deg)` }}>Section 3</div>
        <div className="slice" style={{ transform: `rotate(-288deg)` }}>Section 4</div>
        <div className="slice" style={{ transform: `rotate(-360deg)` }}>Section 5</div>
      </div>
      <button onClick={spinWheel} disabled={spinning}>Spin</button>
    </div>
  );
};

export { Coupon };
