import React, { useState } from 'react';
import './Coupon.css';
import womp from './womp.gif';

const Coupon = () => {
  const [prize, setPrize] = useState(null);
  const [claimed, setClaimed] = useState(false);
  const [showButton, setShowButton] = useState(false);
  const [showGif, setShowGif] = useState(false);

  const spinWheel = () => {
    const options = ['i take u out :3 (u choose place)', 'womp womp', 'unlimited hugs n kisses', 'four dabloons'];
    const randomIndex = Math.floor(Math.random() * options.length);
    const selectedPrize = options[randomIndex];
    setPrize(selectedPrize);
    if (selectedPrize === 'womp womp') {
      setShowButton(true);
    }
  };

  const claimPrize = () => {
    setClaimed(true);
  };

  const handleShowGif = () => {
    setShowGif(true);
  };

  return (
    <div className="d">
      <div className="directions">Click to spin the wheel to see what prize you won &lt;3</div>
      <div className="spin-the-wheel">
        <label>
          <input type="radio" onClick={spinWheel} disabled={prize !== null || claimed} />
          <div className="clicker"></div>
          <div className="wheel">
            <div className="option o1">i take u out :3 (u choose place)</div>
            <div className="option o2">womp womp</div>
            <div className="option o3">unlimited hugs n kisses</div>
            <div className="option o4">four dabloons</div>
          </div>
          {prize && !claimed && (
            <div>
              {prize === 'womp womp' && showButton && !showGif && (
                <button className="message" onClick={handleShowGif}>
                  Claim Prize
                </button>
              )}
              {prize === 'womp womp' && showGif && (
                <img src={womp} alt="womp gif" className="message" />
              )}
              {prize !== 'womp womp' && (
                <button className="message" onClick={claimPrize}>
                  {prize}
                </button>
              )}
            </div>
          )}
        </label>
      </div>
    </div>
  );
};

export { Coupon };
