import React, { useState } from "react";
import './Quiz.css';
import { Link } from 'react-router-dom';

const Quiz = () => {
    const questions = [
        {
            question: "When did I start liking you?",
            options: ["immediately", "fall of 2019", "spring 2022", "summer 2022"]
        },
        {
            question: "Where was our first kiss?",
            options: ["the park", "the library", "the north building", "the movie theater"]
        },
        // Add more questions...
    ];

    const [currentQuestion, setCurrentQuestion] = useState(0);
    const [selectedOption, setSelectedOption] = useState("");
    const [score, setScore] = useState(0);
    const [showScore, setShowScore] = useState(false);

    const handleOptionClick = (option) => {
        setSelectedOption(option);
    };

    const handleNextQuestion = () => {
        // Check if an option was selected
        if (selectedOption) {
            // Check if the selected option is correct
            if (selectedOption === questions[currentQuestion].options[0]) {
                setScore(score + 1);
            }

            setSelectedOption("");
            if (currentQuestion < questions.length - 1) {
                setCurrentQuestion(currentQuestion + 1);
            } else {
                setShowScore(true);
            }
        } else {
            // Show an alert or message to select an option
            alert("Please select an option before moving to the next question.");
        }
    };

    return (
        <div className="quiz-container">
            {showScore ? (
                <div className="result-container">
                    <h2>Your Score: {score} out of {questions.length}</h2>
                    <Link to="/">Back to Home</Link>
                </div>
            ) : (
                <div className="question-container">
                    <div className="question-text">{questions[currentQuestion].question}</div>
                    <div className="options-container">
                        {questions[currentQuestion].options.map((option, index) => (
                            <button
                                key={index}
                                className={`option-button ${selectedOption === option ? 'selected' : ''}`}
                                onClick={() => handleOptionClick(option)}
                            >
                                {option}
                            </button>
                        ))}
                    </div>
                    <button className="next-button" onClick={handleNextQuestion}>Next</button>
                </div>
            )}
        </div>
    );
}

export { Quiz };
