const questions = [
    {
      question: "What is the capital of France?",
      options: ["Paris", "London", "Berlin", "Rome"],
      answer: "Paris"
    },
    {
      question: "Which programming language is known as the 'language of the web'?",
      options: ["JavaScript", "Python", "Java", "C++"],
      answer: "JavaScript"
    },
    {
      question: "What does HTML stand for?",
      options: ["Hyper Text Markup Language", "Hyperlinks and Text Markup Language", "Home Tool Markup Language", "None of the above"],
      answer: "Hyper Text Markup Language"
    }
  ];

  let currentQuestionIndex = 0;

  function showQuestion() {
    const currentQuestion = questions[currentQuestionIndex];
    console.log(currentQuestion.question);
    currentQuestion.options.forEach((option, index) => {
      console.log(`${index + 1}. ${option}`);
    });
  }

  function showResult(selectedAnswer) {
    const currentQuestion = questions[currentQuestionIndex];
    if (selectedAnswer === currentQuestion.answer) {
      console.log('Correct!');
    } else {
      console.log(`Incorrect. The correct answer is: ${currentQuestion.answer}`);
    }
  }

  showQuestion();

  // Example usage
  const selectedAnswer = "Paris"; // Assume the user selected "Paris"
  showResult(selectedAnswer);
