import React from "react";
import './Coupon.css'
import {Link} from 'react-router-dom';
const Coupon = () => {
    const [spinning, setSpinning] = useState(false);
    const [prize, setPrize] = useState(null);

    const options = ['A New Car!', 'A New Boat!', 'A New Skateboard!', 'A New Clipper Card!'];

    const spinWheel = () => {
      setSpinning(true);
      // Generate a random index to select a prize
      const randomIndex = Math.floor(Math.random() * options.length);
      // Simulate spinning for some duration
      setTimeout(() => {
        setPrize(options[randomIndex]);
        setSpinning(false);
      }, 3000); // Adjust duration as needed
    };

    return (
      <div className="spin-container">
        <div className={`wheel ${spinning ? 'spinning' : ''}`}>
          {options.map((option, index) => (
            <div className={`option o${index + 1}`} key={index}>
              {option}
            </div>
          ))}
        </div>
        <div className="directions" onClick={spinWheel}>
          Click to spin the wheel to see what prize you won!
        </div>
        {prize && (
          <div className="message">
            You won {prize}! <br />
            Click to claim your prize.
            <a href="http://votion.co" target="_blank" rel="noopener noreferrer"></a>
          </div>
        )}
      </div>
    );
  };
export {Coupon}
